import { applyUpdate, walkExpression } from "./_internal";
/** Sets the value of a field to the current date. */
export const $currentDate = (obj, expr, arrayFilters = []) => {
    const now = Date.now();
    return walkExpression(expr, arrayFilters, ((_, node, queries) => {
        return applyUpdate(obj, node, queries, (o, k) => {
            o[k] = now;
            return true;
        }, { buildGraph: true });
    }));
};
