import { applyUpdate, walkExpression } from "./_internal";
/** Removes the first or last element of an array. */
export const $pop = (obj, expr, arrayFilters = []) => {
    return walkExpression(expr, arrayFilters, ((val, node, queries) => {
        return applyUpdate(obj, node, queries, (o, k) => {
            const arr = o[k];
            if (!arr.length)
                return false;
            if (val === -1) {
                arr.splice(0, 1);
            }
            else {
                arr.pop();
            }
            return true;
        });
    }));
};
