import { cloneDeep, isEqual } from "../../util";
import { applyUpdate, walkExpression } from "./_internal";
/** Replaces the value of a field with the specified value. */
export const $set = (obj, expr, arrayFilters = []) => {
    return walkExpression(expr, arrayFilters, (val, node, queries) => {
        return applyUpdate(obj, node, queries, (o, k) => {
            if (isEqual(o[k], val))
                return false;
            o[k] = cloneDeep(val);
            return true;
        }, { buildGraph: true });
    });
};
